                                if (f[0].extent[1] > y[d.key].domain()[0])
                                    currentTicks[d.key] = [f[0].extent[1]];
                                if (f[0].extent[0] >= y[d.key].domain()[0])
                                    currentTicks[d.key].push(f[0].extent[0]);
